import React, { useState, useEffect } from "react"
import axios from "axios"

const CharacterDetails = (props) => {

    const [characterDetails, setCharacterDetails] = useState(null)

    useEffect(() => {
        const getDetails = async () => {
            const response = await axios.get(`https://rickandmortyapi.com/api/character/${props.selectedCharacter}`)
            console.log(response)
            setCharacterDetails(response.data.data)
        }
        getDetails()
    }, [props.selectedCharacter])

    return (
        <div>
            {characterDetails ? 
            // add map here
            (
                <div className="details">
                    <div className="character-card">
                    <img src={`${characterDetails[3].image}`} alt="poster" />
                        <h2>{characterDetails.name}</h2>
                        <p>{characterDetails.status}</p>
                        <p>From: {characterDetails[3].origin.name}</p>
                        <p>Species: {characterDetails.species}</p>
                    </div>
                    <button onClick={props.goBack}>Go Back</button>
                </div>
            ) : (
                <h3>Loading...</h3>
            )}
        </div>
)

}

export default CharacterDetails
